(11500, 577, '_list-type_2_img', 'field_6576d935b2ebf'),
(11501, 577, 'list-type_2_title', 'Executive Double'),
(11502, 577, '_list-type_2_title', 'field_6576d9b1b2ec0'),
(11503, 577, 'list-type_2_content', '<div class=\"row\">\r\n<div class=\"col-md-12\">\r\n\r\nTầm nhìn tuyệt đẹp ra quang cảnh thành phố và giường cỡ King thoải mái là những điểm nổi bật của các phòng Executive Double 26 m<sup>2</sup>. Hãy để căn phòng này là điểm xuất phát của bạn trong chuyến thăm tiếp theo của bạn đến Quận 1.\r\n\r\n</div>\r\n</div>'),
(11504, 577, '_list-type_2_content', 'field_6576d9beb2ec1'),
(11505, 577, 'list-type_2_price', ''),
(11506, 577, '_list-type_2_price', 'field_6576da28f3cf3'),
