(11509, 577, 'list-type_3_title', 'Premium Twin'),
(11510, 577, '_list-type_3_title', 'field_6576d9b1b2ec0'),
(11511, 577, 'list-type_3_content', 'Lý tưởng cho những người đi công tác hoặc đi cùng bạn bè và những người có kế hoạch dành nhiều thời gian khám phá thành phố hoặc thực hiện công việc kinh doanh hơn là ở trong phòng. Các phòng Premium Twin rộng 20 m<sup>2</sup> có 2 giường đơn thoải mái và không có cửa sổ.'),
(11512, 577, '_list-type_3_content', 'field_6576d9beb2ec1'),
(11513, 577, 'list-type_3_price', ''),
(11514, 577, '_list-type_3_price', 'field_6576da28f3cf3'),
