(11517, 577, 'list-type_4_title', 'Executive Twin'),
(11518, 577, '_list-type_4_title', 'field_6576d9b1b2ec0'),
(11519, 577, 'list-type_4_content', 'Phòng Executive Twin có 2 giường đơn và tầm nhìn ra quang cảnh thành phố trong không gian rộng 26 m<sup>2</sup>. Bạn bè hoặc khách đi công tác sẽ thích sự sang trọng mà căn phòng ở trung tâm Thành phố Hồ Chí Minh này mang lại.'),
(11520, 577, '_list-type_4_content', 'field_6576d9beb2ec1'),
(11521, 577, 'list-type_4_price', ''),
(11522, 577, '_list-type_4_price', 'field_6576da28f3cf3'),
